#define CACHE_WRITELOCK_WRITELOCK_Locked (0x1UL) /*!< Cache updates locked */


/* Peripheral: CC_AES */
/* Description: CRYPTOCELL AES engine */

/* Register: CC_AES_AES_KEY_0 */
/* Description: Description collection: AES key value to use in non-tunneling operations, or as the first tunnel stage key in tunneling operations.
        The initial AES_KEY_0[0] register holds the least significant bits [31:0] of the key value. */

/* Bits 31..0 : AES non-tunneling or first tunnel stage key value. */
#define CC_AES_AES_KEY_0_VALUE_Pos (0UL) /*!< Position of VALUE field. */
#define CC_AES_AES_KEY_0_VALUE_Msk (0xFFFFFFFFUL << CC_AES_AES_KEY_0_VALUE_Pos) /*!< Bit mask of VALUE field. */

/* Register: CC_AES_AES_KEY_1 */
/* Description: Description collection: AES key value to use as the second tunnel stage key in tunneling operations.
        The initial AES_KEY_1[0] register holds the least significant bits [31:0] of the key value. */

/* Bits 31..0 : AES second tunnel stage key value. */
#define CC_AES_AES_KEY_1_VALUE_Pos (0UL) /*!< Position of VALUE field. */
#define CC_AES_AES_KEY_1_VALUE_Msk (0xFFFFFFFFUL << CC_AES_AES_KEY_1_VALUE_Pos) /*!< Bit mask of VALUE field. */

/* Register: CC_AES_AES_IV_0 */
/* Description: Description collection: AES Initialization Vector (IV) to use in non-tunneling operations, or as the first tunnel stage IV in tunneling operations.
        The initial AES_IV_0[0] register holds the least significant bits [31:0] of the IV. */

/* Bits 31..0 : AES non-tunneling or first tunnel stage IV value. */
#define CC_AES_AES_IV_0_VALUE_Pos (0UL) /*!< Position of VALUE field. */
#define CC_AES_AES_IV_0_VALUE_Msk (0xFFFFFFFFUL << CC_AES_AES_IV_0_VALUE_Pos) /*!< Bit mask of VALUE field. */

/* Register: CC_AES_AES_IV_1 */
/* Description: Description collection: AES Initialization Vector (IV) to use as the second tunnel stage IV in tunneling operations.
        The initial AES_IV_1[0] register holds the least significant bits [31:0] of the IV. */

/* Bits 31..0 : AES second tunnel stage IV value. */
#define CC_AES_AES_IV_1_VALUE_Pos (0UL) /*!< Position of VALUE field. */
#define CC_AES_AES_IV_1_VALUE_Msk (0xFFFFFFFFUL << CC_AES_AES_IV_1_VALUE_Pos) /*!< Bit mask of VALUE field. */

/* Register: CC_AES_AES_CTR */
/* Description: Description collection: AES counter (CTR) to use in non-tunneling and tunneling operations.
        The initial AES_CTR[0] register holds the least significant bits [31:0] of the CTR. */

/* Bits 31..0 : AES CTR value. */
#define CC_AES_AES_CTR_VALUE_Pos (0UL) /*!< Position of VALUE field. */
#define CC_AES_AES_CTR_VALUE_Msk (0xFFFFFFFFUL << CC_AES_AES_CTR_VALUE_Pos) /*!< Bit mask of VALUE field. */

/* Register: CC_AES_AES_BUSY */
/* Description: Status register for AES engine activity. */

/* Bit 0 : AES engine status. */
#define CC_AES_AES_BUSY_STATUS_Pos (0UL) /*!< Position of STATUS field. */
#define CC_AES_AES_BUSY_STATUS_Msk (0x1UL << CC_AES_AES_BUSY_STATUS_Pos) /*!< Bit mask of STATUS field. */
#define CC_AES_AES_BUSY_STATUS_Idle (0x0UL) /*!< AES engine is idle */
#define CC_AES_AES_BUSY_STATUS_Busy (0x1UL) /*!< AES engine is busy */

/* Register: CC_AES_AES_CMAC_INIT */
/* Description: Writing to this address triggers the AES engine to generate K1 and K2 for AES-CMAC operations. */

/* Bit 0 : Generate K1 and K2 for the AES-CMAC operations. */
#define CC_AES_AES_CMAC_INIT_ENABLE_Pos (0UL) /*!< Position of ENABLE field. */
#define CC_AES_AES_CMAC_INIT_ENABLE_Msk (0x1UL << CC_AES_AES_CMAC_INIT_ENABLE_Pos) /*!< Bit mask of ENABLE field. */
#define CC_AES_AES_CMAC_INIT_ENABLE_Enable (0x1UL) /*!< Initialize AES-CMAC operations. */

/* Register: CC_AES_AES_REMAINING_BYTES */
/* Description: This register should be set with the amount of remaining bytes until the end of the current AES operation. */

/* Bits 31..0 : Remaining bytes util the end of the current AES operation. */
#define CC_AES_AES_REMAINING_BYTES_VALUE_Pos (0UL) /*!< Position of VALUE field. */
#define CC_AES_AES_REMAINING_BYTES_VALUE_Msk (0xFFFFFFFFUL << CC_AES_AES_REMAINING_BYTES_VALUE_Pos) /*!< Bit mask of VALUE field. */

/* Register: CC_AES_AES_CONTROL */
/* Description: Control the AES engine behavior. */

/* Bit 31 : Using direct access and not the DIN-DOUT DMA interface */
#define CC_AES_AES_CONTROL_DIRECT_ACCESS_Pos (31UL) /*!< Position of DIRECT_ACCESS field. */
#define CC_AES_AES_CONTROL_DIRECT_ACCESS_Msk (0x1UL << CC_AES_AES_CONTROL_DIRECT_ACCESS_Pos) /*!< Bit mask of DIRECT_ACCESS field. */
#define CC_AES_AES_CONTROL_DIRECT_ACCESS_Disable (0x0UL) /*!< Access using the DIN-DOUT DMA interface */
#define CC_AES_AES_CONTROL_DIRECT_ACCESS_Enable (0x1UL) /*!< Access using direct access */

/* Bit 28 : This field determines for AES-TO-HASH-AND-DOUT tunneling operations, whether the AES outputs to the HASH the result of the first or the second tunneling stage. */
#define CC_AES_AES_CONTROL_AES_OUT_MID_TUN_TO_HASH_Pos (28UL) /*!< Position of AES_OUT_MID_TUN_TO_HASH field. */
#define CC_AES_AES_CONTROL_AES_OUT_MID_TUN_TO_HASH_Msk (0x1UL << CC_AES_AES_CONTROL_AES_OUT_MID_TUN_TO_HASH_Pos) /*!< Bit mask of AES_OUT_MID_TUN_TO_HASH field. */
#define CC_AES_AES_CONTROL_AES_OUT_MID_TUN_TO_HASH_SecondStage (0x0UL) /*!< The AES engine writes to the HASH the result of the second tunnel stage. */
#define CC_AES_AES_CONTROL_AES_OUT_MID_TUN_TO_HASH_FirstStage (0x1UL) /*!< The AES engine writes to the HASH the result of the first tunnel stage. */

/* Bit 26 : This field configure if the input data to the second tunnel stage is to be padded with zeroes according to how many bytes are remaining. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_B1_PAD_EN_Pos (26UL) /*!< Position of AES_TUNNEL_B1_PAD_EN field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_B1_PAD_EN_Msk (0x1UL << CC_AES_AES_CONTROL_AES_TUNNEL_B1_PAD_EN_Pos) /*!< Bit mask of AES_TUNNEL_B1_PAD_EN field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_B1_PAD_EN_Disable (0x0UL) /*!< The data input to the second tunnel stage is not padded with zeros. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_B1_PAD_EN_Enable (0x1UL) /*!< The data input to the second tunnel stage is padded with zeros. */

/* Bit 25 : This fields configure if the AES engine output is the result of the first or second tunneling stage. */
#define CC_AES_AES_CONTROL_AES_OUTPUT_MID_TUNNEL_DATA_Pos (25UL) /*!< Position of AES_OUTPUT_MID_TUNNEL_DATA field. */
#define CC_AES_AES_CONTROL_AES_OUTPUT_MID_TUNNEL_DATA_Msk (0x1UL << CC_AES_AES_CONTROL_AES_OUTPUT_MID_TUNNEL_DATA_Pos) /*!< Bit mask of AES_OUTPUT_MID_TUNNEL_DATA field. */
#define CC_AES_AES_CONTROL_AES_OUTPUT_MID_TUNNEL_DATA_SecondStage (0x0UL) /*!< Output result from the second tunnel stage (standard tunneling). */
#define CC_AES_AES_CONTROL_AES_OUTPUT_MID_TUNNEL_DATA_FirstStage (0x1UL) /*!< Output result from the first tunnel stage. */

/* Bit 24 : Configure if first tunnel stage performs encrypt or decrypt operation. */
#define CC_AES_AES_CONTROL_AES_TUNNEL0_ENCRYPT_Pos (24UL) /*!< Position of AES_TUNNEL0_ENCRYPT field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL0_ENCRYPT_Msk (0x1UL << CC_AES_AES_CONTROL_AES_TUNNEL0_ENCRYPT_Pos) /*!< Bit mask of AES_TUNNEL0_ENCRYPT field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL0_ENCRYPT_Decrypt (0x0UL) /*!< First tunnel stage performs decrypt operations. */
#define CC_AES_AES_CONTROL_AES_TUNNEL0_ENCRYPT_Encrypt (0x1UL) /*!< First tunnel stage performs encrypt operations. */

/* Bit 23 : For tunneling operations this field determine the data that is fed to the second tunneling stage. */
#define CC_AES_AES_CONTROL_AES_TUN_B1_USES_PADDED_DATA_IN_Pos (23UL) /*!< Position of AES_TUN_B1_USES_PADDED_DATA_IN field. */
#define CC_AES_AES_CONTROL_AES_TUN_B1_USES_PADDED_DATA_IN_Msk (0x1UL << CC_AES_AES_CONTROL_AES_TUN_B1_USES_PADDED_DATA_IN_Pos) /*!< Bit mask of AES_TUN_B1_USES_PADDED_DATA_IN field. */
#define CC_AES_AES_CONTROL_AES_TUN_B1_USES_PADDED_DATA_IN_Disable (0x0UL) /*!< The output of the first block is used directly (standard tunneling operation). */
#define CC_AES_AES_CONTROL_AES_TUN_B1_USES_PADDED_DATA_IN_Enable (0x1UL) /*!< The output of the first block is padded before use. */

/* Bit 22 : Configure if first tunnel stage performs encrypt or decrypt operation. */
#define CC_AES_AES_CONTROL_AES_TUNNEL1_DECRYPT_Pos (22UL) /*!< Position of AES_TUNNEL1_DECRYPT field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL1_DECRYPT_Msk (0x1UL << CC_AES_AES_CONTROL_AES_TUNNEL1_DECRYPT_Pos) /*!< Bit mask of AES_TUNNEL1_DECRYPT field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL1_DECRYPT_Encrypt (0x0UL) /*!< Second tunnel stage performs encrypt operations. */
#define CC_AES_AES_CONTROL_AES_TUNNEL1_DECRYPT_Decrypt (0x1UL) /*!< Second tunnel stage performs decrypt operations. */

/* Bits 15..14 : Set the AES key length of the second stage in tunneling operations. */
#define CC_AES_AES_CONTROL_NK_KEY1_Pos (14UL) /*!< Position of NK_KEY1 field. */
#define CC_AES_AES_CONTROL_NK_KEY1_Msk (0x3UL << CC_AES_AES_CONTROL_NK_KEY1_Pos) /*!< Bit mask of NK_KEY1 field. */
