#define CC_AES_AES_CONTROL_NK_KEY1_192Bits (0x1UL) /*!< 192 bits key length */
#define CC_AES_AES_CONTROL_NK_KEY1_256Bits (0x2UL) /*!< 256 bits key length */

/* Bits 13..12 : Set the AES key length in non-tunneling operations, or the AES key length of the first stage in tunneling operations. */
#define CC_AES_AES_CONTROL_NK_KEY0_Pos (12UL) /*!< Position of NK_KEY0 field. */
#define CC_AES_AES_CONTROL_NK_KEY0_Msk (0x3UL << CC_AES_AES_CONTROL_NK_KEY0_Pos) /*!< Bit mask of NK_KEY0 field. */
#define CC_AES_AES_CONTROL_NK_KEY0_128Bits (0x0UL) /*!< 128 bits key length */
#define CC_AES_AES_CONTROL_NK_KEY0_192Bits (0x1UL) /*!< 192 bits key length */
#define CC_AES_AES_CONTROL_NK_KEY0_256Bits (0x2UL) /*!< 256 bits key length */

/* Bit 11 : If MODE_KEY0 is set to CBC, and this field is set, the mode is CBC Bitlocker. */
#define CC_AES_AES_CONTROL_CBC_IS_BITLOCKER_Pos (11UL) /*!< Position of CBC_IS_BITLOCKER field. */
#define CC_AES_AES_CONTROL_CBC_IS_BITLOCKER_Msk (0x1UL << CC_AES_AES_CONTROL_CBC_IS_BITLOCKER_Pos) /*!< Bit mask of CBC_IS_BITLOCKER field. */
#define CC_AES_AES_CONTROL_CBC_IS_BITLOCKER_Disable (0x0UL) /*!< Disable CBC Bitlocker mode */
#define CC_AES_AES_CONTROL_CBC_IS_BITLOCKER_Enable (0x1UL) /*!< Enable CBC Bitlocker mode */

/* Bit 10 : Configure AES engine for standard non-tunneling or tunneling operations. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_Pos (10UL) /*!< Position of AES_TUNNEL field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_Msk (0x1UL << CC_AES_AES_CONTROL_AES_TUNNEL_Pos) /*!< Bit mask of AES_TUNNEL field. */
#define CC_AES_AES_CONTROL_AES_TUNNEL_Disable (0x0UL) /*!< Standard non-tunneling operations */
#define CC_AES_AES_CONTROL_AES_TUNNEL_Enable (0x1UL) /*!< Enable tunneling operations */

/* Bit 8 : If MODE_KEY0 is set to CBC, and this field is set, the mode is CBC with ESSIV. */
#define CC_AES_AES_CONTROL_CBC_IS_ESSIV_Pos (8UL) /*!< Position of CBC_IS_ESSIV field. */
#define CC_AES_AES_CONTROL_CBC_IS_ESSIV_Msk (0x1UL << CC_AES_AES_CONTROL_CBC_IS_ESSIV_Pos) /*!< Bit mask of CBC_IS_ESSIV field. */
#define CC_AES_AES_CONTROL_CBC_IS_ESSIV_Disable (0x0UL) /*!< Disable CBC with ESSIV mode */
#define CC_AES_AES_CONTROL_CBC_IS_ESSIV_Enable (0x1UL) /*!< Enable CBC with ESSIV mode */

/* Bits 7..5 : Set the AES mode of the second stage in tunneling operations */
#define CC_AES_AES_CONTROL_MODE_KEY1_Pos (5UL) /*!< Position of MODE_KEY1 field. */
#define CC_AES_AES_CONTROL_MODE_KEY1_Msk (0x7UL << CC_AES_AES_CONTROL_MODE_KEY1_Pos) /*!< Bit mask of MODE_KEY1 field. */
#define CC_AES_AES_CONTROL_MODE_KEY1_ECB (0x0UL) /*!< Electronic codebook mode */
#define CC_AES_AES_CONTROL_MODE_KEY1_CBC (0x1UL) /*!< Cipher block chaining mode */
#define CC_AES_AES_CONTROL_MODE_KEY1_CTR (0x2UL) /*!< Counter mode */
#define CC_AES_AES_CONTROL_MODE_KEY1_CBC_MAC (0x3UL) /*!< Cipher block chaining message authentication code mode */
#define CC_AES_AES_CONTROL_MODE_KEY1_XEX_XTS (0x4UL) /*!< Xor-Encrypt-Xor (XEX)-based tweaked-codebook mode with ciphertext stealing (XTS) */
#define CC_AES_AES_CONTROL_MODE_KEY1_XCBC_MAC (0x5UL) /*!< AES in CBC mode with extensions to overcome fixed length limitations */
#define CC_AES_AES_CONTROL_MODE_KEY1_OFB (0x6UL) /*!< AES Output FeedBack mode */
#define CC_AES_AES_CONTROL_MODE_KEY1_CMAC (0x7UL) /*!< Cipher-based Message Authentication Code */

/* Bits 4..2 : Set the AES mode in non-tunneling operations, or the AES mode of the first stage in tunneling operations. */
#define CC_AES_AES_CONTROL_MODE_KEY0_Pos (2UL) /*!< Position of MODE_KEY0 field. */
#define CC_AES_AES_CONTROL_MODE_KEY0_Msk (0x7UL << CC_AES_AES_CONTROL_MODE_KEY0_Pos) /*!< Bit mask of MODE_KEY0 field. */
#define CC_AES_AES_CONTROL_MODE_KEY0_ECB (0x0UL) /*!< Electronic codebook mode */
#define CC_AES_AES_CONTROL_MODE_KEY0_CBC (0x1UL) /*!< Cipher block chaining mode */
#define CC_AES_AES_CONTROL_MODE_KEY0_CTR (0x2UL) /*!< Counter mode */
